package org.example.controller;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpSession;import lombok.RequiredArgsConstructor;import org.example.dao.MovieDao;import org.example.entity.MovieEntity;import org.springframework.beans.support.PagedListHolder;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.ServletRequestUtils;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import java.util.List;@Controller@RequestMapping("/movie")@RequiredArgsConstructorpublic class MovieController {    private final MovieDao movieDao;    @GetMapping("/list")    public String getMovieList(            Model model,            HttpServletRequest httpServletRequest    ){        HttpSession session = httpServletRequest.getSession();        List<MovieEntity> movieList = movieDao.findAll();        model.addAttribute("movieList", movieList);        return "movie";    }}